import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import Form2 from "./components/Form2";
import Form3 from "./components/Form3";
import ParkInfo from "./components/ParkInfo";
import VisitorCenters from "./components/VisitorCenters";
import Articles from "./components/Articles";
import Titles2 from "./components/Titles2";
import Titles3 from "./components/Titles3";
//to do: alerts,visitor centers,articles,events,news releases,educational information
const API_KEY = "kHjKwA8UlJlW5B9IuGAc42is8eOVzuqiqOP7OVS4";

class App extends React.Component {

  state = {
    name: undefined,
    fullName: undefined,
    states: undefined,
    designation: undefined,
    weatherInfo: undefined,
    latLong: undefined,
    gDescription: undefined,
    error: undefined
  }

  state = {//picture
    image: undefined,
    caption: undefined,
    pTitle: undefined
  }

  state = {//alerts
    aTitle:undefined,
    aDescription: undefined,
    category: undefined
  }

  state = { //articles
    arTitle: undefined,
    arUrl: undefined,
    arTitle2: undefined,
    arUrl2: undefined,
    arTitle3: undefined,
    arUrl3: undefined
  }

  state = {//events
    location: undefined,
    contactname: undefined,
    start: undefined,
    end: undefined
  }

  state = {//educational info
    objective: undefined,
    subject: undefined,
    vidTitle: undefined,
    edUrl: undefined
  }

  state = {//news
    abstract: undefined,
    nUrl: undefined,
    nDate: undefined
  }

  state = {//visitorcenters
    vName: undefined,
    vDescription: undefined,
    vUrl: undefined,
    vName2: undefined,
    vDescription2: undefined,
    vUrl2: undefined,
    vName3: undefined,
    vDescription3: undefined,
    vUrl3: undefined,
  }

  getInfo = async (e) => {
    //inital data call
    e.preventDefault();
    const stateCode = e.target.elements.stateCode.value;
    const parkCode = e.target.elements.parkCode.value;

     if (stateCode || parkCode) {
    const api_call = await fetch(`https://developer.nps.gov/api/v1/parks?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
    const data = await api_call.json();
    const count = 0
    console.log(data);

      this.setState({ //general
        name: data.data[count].name,
        fullName: data.data[count].fullName,
        states: data.data[count].states,
        designation: data.data[count].designation,
        weatherInfo: data.data[count].weatherInfo,
        latLong: data.data[count].latLong,
        gDescription: data.data[count].description,
        error: ""
      });

    } else {
      this.setState({
      name: undefined,
      fullName: undefined,
      states: undefined,
      designation: undefined,
      weatherInfo: undefined,
      latLong: undefined,
      gDescription: undefined,
      image: undefined,
      caption: undefined,
      title: undefined,
      error: "Please enter a state or a park code"
    });
  }
  }

  getVisitorCenters = async (e) => {
    e.preventDefault();
    //visitor center call
    if (this.getInfo.stateCode || this.getInfo.parkCode) {
    const api_call2 = await fetch(`https://developer.nps.gov/api/v1/visitorcenters?parkCode=${this.getInfo.parkCode}&stateCode=${this.getInfo.stateCode}&api_key=${API_KEY}`);
    const data2 = await api_call2.json();
    console.log(data2);

      this.setState({ //visitor centers
      vName: data2.data[0].name,
      vDescription: data2.data[0].description,
      vUrl: data2.data[0].url,
      vName2: data2.data[1].name,
      vDescription2: data2.data[1].description,
      vUrl2: data2.data[1].url,
      vName3: data2.data[0].name,
      vDescription3: data2.data[2].description,
      vUrl3: data2.data[2].url
    });
  } else {
    this.setState({
    vName: undefined,
    vDescription: undefined,
    vUrl: undefined,
    vName2: undefined,
    vDescription2: undefined,
    vUrl2: undefined,
    vName3: undefined,
    vDescription3:undefined,
    vUrl3: undefined
  });
  }
  }

  getArticles = async (e) => {
    e.preventDefault();
    //articles call
    if (this.getInfo.stateCode || this.getInfo.parkCode) {
      const api_call3 = await fetch(`https://developer.nps.gov/api/v1/articles?parkCode=${this.getInfo.parkCode}&stateCode=${this.getInfo.stateCode}&api_key=${API_KEY}`);
      const data3 = await api_call3.json();
      console.log(data3);
      this.setState({
        arTitle: data3.data[0].title,
        arUrl: data3.data[0].url,
        arTitle2: data3.data[1].title,
        arUrl2: data3.data[1].url,
        arTitle3: data3.data[2].title,
        arUrl3: data3.data[2].url
      });
    } else {
      this.setState({
      arTitle: undefined,
      arUrl: undefined,
      arTitle2: undefined,
      arUrl2: undefined,
      arTitle3: undefined,
      arUrl3: undefined
    });
    }
  }
  render() {

    return (
      <div>
        <div className = "wrapper">
          <div className = "main">
            <div className = "container">
              <div className = "row">
                <div className = "col title-container">
                  <Titles />
                </div>

                <div className = "col form-container">
                <Form getInfo ={this.getInfo}/>
                <ParkInfo
                  name = {this.state.name}
                  states = {this.state.states}
                  designation = {this.state.designation}
                  latLong = {this.state.latLong}
                  gDescription = {this.state.gDescription}
                  error = {this.state.error}
                 />
                 </div>
                </div>
              </div>
            </div>
          </div>
          <div className = "wrapper">
            <div className = "main">
              <div className = "container">
                <div className = "row">
                  <div className = "col form-container">
                   <Titles2 />
                  </div>
                  <div className = "col form-container">
                  <Form2 getVisitorCenters = {this.getVisitorCenters}/>
                   <VisitorCenters
                    vName = {this.state.vName}
                    vDescription = {this.state.vDescription}
                    vUrl = <a href = "Click here for more info!">{this.state.vUrl}</a>
                    vName2 = {this.state.vName2}
                    vDescription2 = {this.state.vDescription2}
                    vUrl2 = <a href = "Click here for more info!">{this.state.vUrl2} </a>
                    />
                   </div>
                  </div>
                </div>
              </div>
            </div>
            <div className = "wrapper">
              <div className = "main">
                <div className = "container">
                  <div className = "row">
                    <div className = "col form-container">
                     <Titles3 />
                    </div>
                    <div className = "col form-container">
                      <Form3 getArticles = {this.getArticles}/>
                     <Articles
                      arTitle = {this.state.arTitle}
                      arUrl = <a href = "Click here for more info!">{this.state.arUrl}</a>
                      arTitle2 = {this.state.arTitle2}
                      arUrl2 = <a href = "Click here for more info!">{this.state.arUrl2}</a>
                      arTitle3 = {this.state.arTitle3}
                      arUrl3 = <a href = "Click here for more info!">{this.state.arUrl3}</a>
                      />
                     </div>
                    </div>
                  </div>
                </div>
              </div>
        </div>
    );
  }
};
export default App;
