import React from "react";
import Form from "./components/Form";
import ParkInfo from "./components/ParkInfo";
import VisitorCenters from "./components/VisitorCenters";
import Articles from "./components/Articles";
import NewsReleases from "./components/NewsReleases";
import EduInfo from "./components/EduInfo";
import Events from "./components/Events";
import Alerts from "./components/Alerts";
import Home from "./components/Home";
import VCTitles from "./components/VCTitles";
import ArTitles from "./components/ArTitles";
import NRTitles from "./components/NRTitles";
import EduTitles from "./components/EduTitles";
import EvTitles from "./components/EvTitles";
import AlrtTitles from "./components/Titles7";
//to do: pictures utilize lat/long coords
const API_KEY = "kHjKwA8UlJlW5B9IuGAc42is8eOVzuqiqOP7OVS4";

class App extends React.Component {

  state = { //DONE
    name: undefined,
    fullName: undefined,
    states: undefined,
    designation: undefined,
    weatherInfo: undefined,
    latLong: undefined,
    gDescription: undefined,
    error: undefined
  }

  state = {//picture
    image: undefined,
    caption: undefined,
    pTitle: undefined
  }

  state = {//alerts DONE
    aTitle:undefined,
    aDescription: undefined,
    category: undefined,
    aTitle2:undefined,
    aDescription2: undefined,
    category2: undefined,
    aError: undefined
  }

  state = { //articles DONE
    arTitle: undefined,
    arUrl: undefined,
    arTitle2: undefined,
    arUrl2: undefined,
    arTitle3: undefined,
    arUrl3: undefined,
    arError: undefined
  }

  state = {//events DONE
    location: undefined,
    evTitle: undefined,
    contactPhone: undefined,
    start: undefined,
    end: undefined,
    evDescription: undefined,
    evError: undefined
  }

  state = {//educational info DONE
    objective: undefined,
    subject: undefined,
    vidTitle: undefined,
    edUrl: undefined,
    edError: undefined
  }

  state = {//news DONE
    abstract: undefined,
    nUrl: undefined,
