import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import ParkInfo from "./components/ParkInfo";

const API_KEY = "kHjKwA8UlJlW5B9IuGAc42is8eOVzuqiqOP7OVS4";

class App extends React.Component {

  state = {
    name: undefined,
    fullName: undefined,
    designation: undefined,
    weatherInfo: undefined,
    latLong: undefined,
    description: undefined,
    error: undefined
  }

  getInfo = async (e) => {
    e.preventDefault();
    const stateCode = e.target.elements.stateCode.value;
    const parkCode = e.target.elements.parkCode.value;
    const api_call = await fetch(`https://developer.nps.gov/api/v1/parks?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
    const data = await api_call.json();
    console.log(data);
    this.setState({
      name: data[0].name,
      fullName: data.fullName,
      designation: data.designation,
      weatherInfo: data.weatherInfo,
      latLong: data.latLong,
      description: data.description,
      error: ""
    });
  }
  render() {

    return (
      <div>
        <Titles />
        <Form getInfo ={this.getInfo}/>
        <ParkInfo
          name = {this.state.name}
          fullName = {this.state.fullName}
          designation = {this.state.designation}
          weatherInfo = {this.state.weatherInfo}
          latLong = {this.state.latLong}
          description = {this.state.description}
          error = {this.state.error}
         />
      </div>
    );

  }

}

export default App;
