import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import ParkInfo from "./components/ParkInfo";
import ParkPics from "./components/ParkPics";
import Form2 from "./components/Form2";
//to do: alerts,visitor centers,articles,events,news releases,educational information
const API_KEY = "kHjKwA8UlJlW5B9IuGAc42is8eOVzuqiqOP7OVS4";

class App extends React.Component {

  state = {
    name: undefined,
    fullName: undefined,
    states: undefined,
    designation: undefined,
    weatherInfo: undefined,
    latLong: undefined,
    gDescription: undefined,
    error: undefined
  }

  state = {//picture
    image: undefined,
    caption: undefined,
    pTitle: undefined
  }

  state = {//alerts
    aTitle:undefined,
    aDescription: undefined,
    category: undefined
  }

  state = { //articles
    arTitle: undefined,
    arUrl: undefined
  }

  state = {//events
    location: undefined,
    contactname: undefined,
    contactname: undefined,
    start: undefined,
    end: undefined
  }

  state = {//educational info
    objective: undefined,
    subject: undefined,
    vidTitle: undefined,
    edUrl: undefined
  }

  state = {//news
    abstract: undefined,
    nUrl: undefined,
    nDate: undefined
  }

  getInfo = async (e) => {
    //inital data call
    e.preventDefault();
    const stateCode = e.target.elements.stateCode.value;
    const parkCode = e.target.elements.parkCode.value;

     if (stateCode || parkCode) {
    const api_call = await fetch(`https://developer.nps.gov/api/v1/parks?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
    const data = await api_call.json();
    const count = 0
    const count2 = 0;
    console.log(data);

      this.setState({
        name: data.data[count].name,
        fullName: data.data[count].fullName,
        states: data.data[count].states,
        designation: data.data[count].designation,
        weatherInfo: data.data[count].weatherInfo,
        latLong: data.data[count].latLong,
        gDescription: data.data[count].description,
        error: ""
      });

      //image call
      const api_call2 = await fetch(`https://developer.nps.gov/api/v1/places?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`); //get image from site
      const data2 = await api_call2.json();
      console.log(data2);
      this.setState({
        image: data.data[count2].url[1],
        caption: data.data[count2].listingdescription,
        title: data.data[count2].altText
      });
    } else {
      this.setState({
      name: undefined,
      fullName: undefined,
      states: undefined,
      designation: undefined,
      weatherInfo: undefined,
      latLong: undefined,
      gDescription: undefined,
      error: "Please enter a state or a park code"
    });
  }
  }
  render() {

    return (
      <div>
        <div className = "wrapper">
          <div className = "main">
            <div className = "container">
              <div className = "row">
                <div className = "col title-container">
                  <Titles />
                </div>

                <div className = "col form-container">
                <Form getInfo ={this.getInfo}/>
                <ParkInfo
                  name = {this.state.name}
                  states = {this.state.states}
                  designation = {this.state.designation}
                  latLong = {this.state.latLong}
                  gDescription = {this.state.gDescription}
                  error = {this.state.error}
                 />
                 </div>
                </div>
              </div>
            </div>
          </div>
          <div className = "wrapper">
            <div className = "main">
              <div className = "container">
                <div className = "row">
                  <div className = "col form-container">
                  <Form2 nextPic ={this.state.image}/>
                  <ParkPics
                    image = {this.state.image}
                   />
                   </div>
                  </div>
                </div>
              </div>
            </div>
        </div>
    );
  }
};
export default App;
