import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import ParkInfo from "./components/ParkInfo";
import VisitorCenters from "./components/VisitorCenters";
import Articles from "./components/Articles";
import NewsReleases from "./components/NewsReleases";
import EduInfo from "./components/EduInfo";
import Events from "./components/Events";
import Alerts from "./components/Alerts";
import Titles2 from "./components/Titles2";
import Titles3 from "./components/Titles3";
import Titles4 from "./components/Titles4";
import Titles5 from "./components/Titles5";
import Titles6 from "./components/Titles6";
import Titles7 from "./components/Titles7";
//to do: pictures, handle errors for if theres no info to be retrived, make it  easier for users to search by park name, utilize lat/long coords
const API_KEY = "kHjKwA8UlJlW5B9IuGAc42is8eOVzuqiqOP7OVS4";

class App extends React.Component {

  state = { //DONE
    name: undefined,
    fullName: undefined,
    states: undefined,
    designation: undefined,
    weatherInfo: undefined,
    latLong: undefined,
    gDescription: undefined,
    error: undefined
  }

  state = {//picture
    image: undefined,
    caption: undefined,
    pTitle: undefined
  }

  state = {//alerts DONE
    aTitle:undefined,
    aDescription: undefined,
    category: undefined,
    aTitle2:undefined,
    aDescription2: undefined,
    category2: undefined
  }

  state = { //articles DONE
    arTitle: undefined,
    arUrl: undefined,
    arTitle2: undefined,
    arUrl2: undefined,
    arTitle3: undefined,
    arUrl3: undefined
  }

  state = {//events DONE
    location: undefined,
    evTitle: undefined,
    contactPhone: undefined,
    start: undefined,
    end: undefined,
    evDescription: undefined
  }

  state = {//educational info DONE
    objective: undefined,
    subject: undefined,
    vidTitle: undefined,
    edUrl: undefined
  }

  state = {//news DONE
    abstract: undefined,
    nUrl: undefined,
    nDate: undefined,
    abstract2: undefined,
    nUrl2: undefined,
    nDate2: undefined
  }

  state = {//visitorcenters DONE
    vName: undefined,
    vDescription: undefined,
    vUrl: undefined,
    vName2: undefined,
    vDescription2: undefined,
    vUrl2: undefined,
    vName3: undefined,
    vDescription3: undefined,
    vUrl3: undefined,
  }

  getInfo = async (e) => {
    //inital data call
    e.preventDefault();
    const stateCode = e.target.elements.stateCode.value;
    const parkCode = e.target.elements.parkCode.value;

     if (stateCode || parkCode) {
    const api_call = await fetch(`https://developer.nps.gov/api/v1/parks?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
    const data = await api_call.json();
    const count = 0
    if (data.data.total < 1) {
      this.setState({
      error: "No parks found for given inputs, check for spelling mistakes and try again"
    });
    } else {
    console.log(data);

      this.setState({ //general
        name: data.data[count].name,
        fullName: data.data[count].fullName,
        states: data.data[count].states,
        designation: data.data[count].designation,
        weatherInfo: data.data[count].weatherInfo,
        latLong: data.data[count].latLong,
        gDescription: data.data[count].description,
        error: ""
      });
    }
      const api_call2 = await fetch(`https://developer.nps.gov/api/v1/visitorcenters?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
      const data2 = await api_call2.json();
      console.log(data2);

        this.setState({ //visitor centers
        vName: data2.data[0].name,
        vDescription: data2.data[0].description,
        vUrl: data2.data[0].url,
        vName2: data2.data[1].name,
        vDescription2: data2.data[1].description,
        vUrl2: data2.data[1].url,
        vName3: data2.data[0].name,
        vDescription3: data2.data[2].description,
        vUrl3: data2.data[2].url
      });

      const api_call3 = await fetch(`https://developer.nps.gov/api/v1/articles?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
      const data3 = await api_call3.json();
      console.log(data3);
      this.setState({ //articles
        arTitle: data3.data[0].title,
        arUrl: data3.data[0].url,
        arTitle2: data3.data[1].title,
        arUrl2: data3.data[1].url,
        arTitle3: data3.data[2].title,
        arUrl3: data3.data[2].url
      });

      const api_call4 = await fetch(`https://developer.nps.gov/api/v1/newsreleases?parkCode=${parkCode}&stateCode=${stateCode}&api_key=${API_KEY}`);
