import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import ParkInfo from "./components/ParkInfo";

const API_KEY = "kHjKwA8UlJlW5B9IuGAc42is8eOVzuqiqOP7OVS4";

class App extends React.Component {

  state = {

  }
  getInfo = async (e) => {
    e.preventDefault();
    const stateCode = e.target.elements.stateCode.value;
    const api_call = await fetch(`https://developer.nps.gov/api/v1/parks?stateCode=${stateCode}&api_key=${API_KEY}`);
    const data = await api_call.json();
    console.log(data);
  }
  render() {

    return (
      <div>
        <Titles />
        <Form getInfo ={this.getInfo}/>
        <ParkInfo />
      </div>
    );

  }

}

export default App;
